import React from 'react';
import Banner from '../../components/Banner/Banner';
import Card from '../../components/Card/Card';
import '../../components/Banner/Banner.css'; 
import '../../components/Card/Card.css'; 
import bannerImage from '../../assets/images/Banner1.png';
import logo from '../../assets/images/logo.png'; // Логото за Header
import footerLogo from '../../assets/images/logo.png'; // Логото за Footer

const Home = () => {
  const cardData = [
    { description: 'Titre de la location ' },
    { description: 'Titre de la location ' },
    { description: 'Titre de la location ' },
    { description: 'Titre de la location' },
    { description: 'Titre de la location' },
    { description: 'Titre de la location' },
  ];

  const headerData = {
    logoPath: logo,
    links: [
      { path: '/', label: 'Accueil' },
      { path: '/about', label: 'À propos' }
    ]
  };

  const footerData = {
    logoPath: footerLogo,
    text: '© 2020 Kasa. All rights reserved'
  };

  return (
    <>
      <header className="home-header">
        <img src={headerData.logoPath} alt="Kasa logo" className="home-logo" />
        <nav className="home-nav">
          {headerData.links.map((link, index) => (
            <NavLink
              key={index}
              to={link.path}
              className={`home-nav-link ${location.pathname === link.path ? "underline-on-active" : ""}`}
            >
              {link.label}
            </NavLink>
          ))}
        </nav>
      </header>
      <div className="home">
        <Banner title="Chez vous, partout et ailleurs" image={bannerImage} />
        <div className="home-content">
          <div className="cards-container">
            {cardData.map((card, index) => (
              <Card 
                key={index} 
                description={card.description} 
              />
            ))}
          </div>
        </div>
      </div>
      <footer className="home-footer">
        <img src={footerData.logoPath} alt="Kasa logo footer" className="footer-logo" />
        <p className="footer-text">{footerData.text}</p>
      </footer>
    </>
  );
};

export default Home;
