import React from 'react';
import { render, screen } from '@testing-library/react';
import Home from './Home'; // Assurez-vous que le chemin est correct
import logementsData from '../../assets/logements.json';

describe('Home component', () => {
  test('renders banner title', () => {
    render(<Home />);
    const bannerTitle = screen.getByText(/Chez vous, partout et ailleurs/i);
    expect(bannerTitle).toBeInTheDocument();
  });

  test('renders cards', () => {
    render(<Home />);
    const cardTitles = logementsData.slice(0, 6).map(item => item.title);
    cardTitles.forEach(title => {
      const cardTitle = screen.getByText(title);
      expect(cardTitle).toBeInTheDocument();
    });
  });
});
