import React from 'react';
import { useParams } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import logementsData from '../assets/logements.json';
import NotFound from '../pages/NotFound/NotFound';
import Collapse from '../components/Collapse/Collapse';

const FicheLogement = () => {
  const { id } = useParams();

  const logement = logementsData.find(item => item.id === id);

  if (!logement) {
    return <NotFound />;
  }

  return (
    <div>
      <Carousel
        showArrows={true}
        showIndicators={false}
        showStatus={false}
        showThumbs={false}
        infiniteLoop={true}
        autoPlay={false} // Исклучете автоматско ротирање
      >
        {logement.pictures.map((picture, index) => (
          <div key={index}>
            <img src={picture} alt={`View of ${logement.title} - ${index + 1}`} />
          </div>
        ))}
      </Carousel>
      <h1>{logement.title}</h1>
      <p>{logement.description}</p>
      <p>Host: {logement.host.name}</p>
      <img src={logement.host.picture} alt={`Host ${logement.host.name}`} />
      <div className="collapses-container">
        <Collapse title="Description">
          <p>
            Yous serez a 50m du canal Saint-Martin ou vous pourrez pique-niquer l'ete et a cote de nombreux bars et restaurants. Au cceur de Paris avec 5 lignes de metro et de nombreux bus. Logement parfait pour les voyageurs en solo et les voyageurs d'affaires. Yous etes al station de la gare de l'est (7 minutes a pied).
          </p>
        </Collapse>
        <Collapse title="Équipements">
          <p>
            Climatisation Wi-Fi Cuisine Espace de travail Fer à repasser Sèche-cheveux Cintres
          </p>
        </Collapse>
      </div>
    </div>
  );
};

export default FicheLogement;
