import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import logementsData from '../assets/logements.json';
import NotFound from '../pages/NotFound/NotFound';
import Collapse from '../components/Collapse/Collapse';

const FicheLogement = () => {
  const { id } = useParams();
  const [selectedItem, setSelectedItem] = useState(0);

  const logement = logementsData.find(item => item.id === id);

  if (!logement) {
    return <NotFound />;
  }

  const handleNext = () => {
    setSelectedItem((selectedItem + 1) % logement.pictures.length);
  };

  const handlePrev = () => {
    setSelectedItem((selectedItem - 1 + logement.pictures.length) % logement.pictures.length);
  };

  return (
    <div>
      <Carousel 
        selectedItem={selectedItem}
        onChange={index => setSelectedItem(index)}
        infiniteLoop={false} // disable default looping
        showArrows={true}
        renderArrowPrev={(clickHandler, hasPrev) => (
          <button
            type="button"
            onClick={handlePrev}
          >
            &lt;
          </button>
        )}
        renderArrowNext={(clickHandler, hasNext) => (
          <button
            type="button"
            onClick={handleNext}
          >
            &gt;
          </button>
        )}
      >
        {logement.pictures.map((picture, index) => (
          <div key={index}>
            <img src={picture} alt={`View of ${logement.title} - ${index + 1}`} />
          </div>
        ))}
      </Carousel>
      <h1>{logement.title}</h1>
      <p>{logement.description}</p>
      <p>Host: {logement.host.name}</p>
      <img src={logement.host.picture} alt={`Host ${logement.host.name}`} />
      <div className="collapses-container">
        <Collapse title="Description">
          <p>
            Vous serez à 50m du canal Saint-Martin où vous pourrez pique-niquer l'été et à côté de nombreux bars et restaurants. Au cœur de Paris avec 5 lignes de métro et de nombreux bus. Logement parfait pour les voyageurs en solo et les voyageurs d'affaires. Vous êtes à la station de la gare de l'est (7 minutes à pied).
          </p>
        </Collapse>
        <Collapse title="Équipements">
          <p>
            Climatisation
            Wi-Fi
            Cuisine
            Espace de travail
            Fer à repasser
            Sèche-cheveux
            Cintres
          </p>
        </Collapse>
      </div>
    </div>
  );
};

export default FicheLogement;
