import React, { useState, useEffect } from 'react';
import './Collapse.css';

const Collapse = ({ title, children, className }) => {
  const [isOpen, setIsOpen] = useState(false);

  // Učitavanje stanja iz localStorage prilikom prvog renderiranja
  useEffect(() => {
    const storedState = localStorage.getItem('collapseState');
    if (storedState !== null) {
      setIsOpen(JSON.parse(storedState));
    }
  }, []);

  // Čuvanje stanja u localStorage kada se promijeni isOpen
  useEffect(() => {
    localStorage.setItem('collapseState', JSON.stringify(isOpen));
  }, [isOpen]);

  const toggleCollapse = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`collapse ${className} ${isOpen ? 'open' : ''}`}>
      <div className="collapse-header" onClick={toggleCollapse}>
        <h3 className="collapse-title">{title}</h3>
        <span className="arrow">
          <i className={`fas ${isOpen ? 'fa-chevron-down' : 'fa-chevron-up'}`}></i>
        </span>
      </div>
      <div className="collapse-content">
        {children}
      </div>
    </div>
  );
};

export default Collapse;
