import React, { useState } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import Carrousel from '../components/Carrousel/Carrousel';
import Collapse from '../components/Collapse/Collapse';
import Rating from '../components/Rating/Rating';
import Host from '../components/Host/Host';
import logementsData from '../assets/logements.json';

const FicheLogement = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const { pictures, title, location, host, tags, ratings } = logementsData[currentImageIndex];

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % logementsData.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + logementsData.length) % logementsData.length);
  };

  return (
    <div className="fiche-logement">
      <Header />
      <main className="content">
        <Carrousel pictures={pictures} onNext={nextImage} onPrev={prevImage} />
        <div className="details">
          <h2>{title}</h2>
          <p>{location}</p>
          <Host host={host} />
          <Rating ratings={ratings} />
          <Collapse title="Tags">
            <ul>
              {tags.map((tag, index) => (
                <li key={index}>{tag}</li>
              ))}
            </ul>
          </Collapse>
          <Collapse title="Details">
          </Collapse>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default FicheLogement;
