import React from 'react';
import { useParams } from 'react-router-dom';
import logementsData from '../../assets/logements.json';
import NotFound from '../NotFound/NotFound';
import Collapse from '../../components/Collapse/Collapse';
import CarouselComponent from '../../components/Carousel/Carousel';
import './Fiche-Logement.css';


const FicheLogement = () => {
  const { id } = useParams();

  const logement = logementsData.find(item => item.id === id);

  if (!logement) {
    return <NotFound />;
  }

  const uniquePictures = Array.from(new Set(logement.pictures));

  return (
    <main>
      <CarouselComponent pictures={uniquePictures} title={logement.title} />
      <h1>{logement.title}</h1>
      <p>{logement.location}</p>
      <p>{logement.description}</p>
      <p>Host: {logement.host.name}</p>
      <img src={logement.host.picture} alt={`Host ${logement.host.name}`} />
      <p>{logement.rating}</p>
      <div className="collapses-container-log">
        <Collapse title="Description" className="collapse-logement">
          <div className="collapse-logement-content">
            <p>Yous serez a 50m du canal Saint-Martin ou vous pourrez
                pique-niquer l'ete et a cote de nombreux bars et
                restau rants. Au cceur de Paris avec 5 lignes de metro et de
                nombreux bus. Logement parfait pour les voyageurs en
                solo et les voyageurs d'affaires. Yous etes al station de la
                gare de l'est (7 minutes a pied).
            </p>
          </div>
        </Collapse>
        <Collapse title="Équipements" className="collapse-logement">
          <div className="collapse-logement-content">
            <p>Climatisation
                Wi-Fi
                Cuisine
                Espace de travail
                Fer à repasser
                Sèche-cheveux
                Cintres
            </p>
          </div>
        </Collapse>
      </div>
    </main>
  );
};

export default FicheLogement;
