import React, { useState } from 'react';
import './Carousel.css';

const CarouselComponent = ({ pictures, title }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === pictures.length - 1 ? 0 : prevSlide + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? pictures.length - 1 : prevSlide - 1));
  };

  return (
    <div className="carousel-container">
      <div className="arrow left-arrow" onClick={prevSlide}>
        &lt;
      </div>
      <div className="carousel">
        {pictures.map((picture, index) => (
          <div key={index} className={index === currentSlide ? 'slide active' : 'slide'}>
            <img src={picture} alt={`View of ${title} - ${index + 1}`} />
          </div>
        ))}
      </div>
      <div className="arrow right-arrow" onClick={nextSlide}>
        &gt;
      </div>
    </div>
  );
};

export default CarouselComponent;
