import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import logo from '../../assets/images/logo.png';
import './Header.css';

const Header = () => {
  const location = useLocation();

  return (
    <header className="home-header">
      <img src={logo} alt="Kasa logo" className="home-logo" />
      <nav className="home-nav">
        <Link to="/" className={`home-nav-link ${location.pathname === "/" && "underline-on-active"}`}>Accueil</Link>
        <Link to="/about" className={`home-nav-link ${location.pathname === "/about" && "underline-on-active"}`}>Ã€ propos</Link>
      </nav>
    </header>
  );
};

export default Header;
