// Carousel.js

import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Carousel.css';

const CarouselComponent = ({ pictures, title }) => {
  const showArrows = pictures.length > 1;

  return (
    <div className="carousel-container">
      <Carousel
        showArrows={showArrows}
        showThumbs={false}
        showStatus={showArrows}
        showIndicators={showArrows}
        infiniteLoop={true}
        stopOnHover={false}
        renderArrowPrev={(onClickHandler, hasPrev) =>
          hasPrev && (
            <div className="carousel-chevron carousel-chevron-left" onClick={onClickHandler}>
              <span>&lt;</span>
            </div>
          )
        }
        renderArrowNext={(onClickHandler, hasNext) =>
          hasNext && (
            <div className="carousel-chevron carousel-chevron-right" onClick={onClickHandler}>
              <span>&gt;</span>
            </div>
          )
        }
      >
        {pictures.map((picture, index) => (
          <div key={index}>
            <img src={picture} alt={`View of ${title} - ${index + 1}`} />
          </div>
        ))}
      </Carousel>
    </div>
  );
};

export default CarouselComponent;
