import React, { useState } from 'react';
import Banner from '../../components/Banner/Banner';
import Card from '../../components/Card/Card';
import '../../components/Banner/Banner.css'; 
import '../../components/Card/Card.css'; 
import bannerImage from '../../assets/images/Banner1.png';
import logementsData from '../../assets/logements.json'; 
import './Home.css'

const Home = () => {
  const [visibleCards, setVisibleCards] = useState(6);

  const cardData = logementsData.map(item => ({
    id: item.id,
    title: item.title,
    image: item.cover
  }));

  const handleLoadMore = () => {
    setVisibleCards(prevVisibleCards => prevVisibleCards + 6);
  };

  return (
    <main>
      <Banner title="Chez vous, partout et ailleurs" image={bannerImage} />
      <div className="home-content">
        <div className="cards-container">
          {cardData.slice(0, visibleCards).map((card, index) => (
            <Card 
              key={index} 
              id={card.id}
              title={card.title} 
              image={card.image} 
            />
          ))}
        </div>
        {visibleCards < cardData.length && (
          <button onClick={handleLoadMore} className="load-more-button">Load More</button>
        )}
      </div>
    </main>
  );
};

export default Home;
