import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Carousel.css';
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa'; // Импортирајте иконите

const CarouselComponent = ({ pictures, title }) => {
  const showArrows = pictures.length > 1;

  return (
    <div className="carousel-container">
      {/* Лева стрелка */}
      <div className="carousel-chevron carousel-chevron-left" onClick={() => {}}>
        <FaChevronLeft />
      </div>

      {/* Carousel компонента */}
      <Carousel
        showArrows={false} // Исклучете ги вградените стрелки
        showThumbs={false}
        showStatus={showArrows}
        showIndicators={showArrows}
        infiniteLoop={true}
        stopOnHover={false}
      >
        {pictures.map((picture, index) => (
          <div key={index}>
            <img src={picture} alt={`View of ${title} - ${index + 1}`} />
          </div>
        ))}
      </Carousel>

      {/* Десна стрелка */}
      <div className="carousel-chevron carousel-chevron-right" onClick={() => {}}>
        <FaChevronRight />
      </div>
    </div>
  );
};

export default CarouselComponent;
