import React from 'react';
import { useParams } from 'react-router-dom';
import logementsData from '../assets/logements.json';
import NotFound from '../pages/NotFound/NotFound';

const FicheLogement = () => {
  const { id } = useParams();
  const parsedId = parseInt(id); // Pretvori id u broj

  // Proveri da li parsedId nije NaN i da li postoji logement sa tim id
  const logement = logementsData.find(item => item.id === parsedId);

  if (!logement) {
    return <NotFound />;
  }

  return (
    <div>
      <h1>{logement.title}</h1>
      <p>{logement.description}</p>
      <p>Host: {logement.host}</p>
      {/* Dodajte ostale informacije kao Å¡to su slike, ocene, tagovi, itd. */}
    </div>
  );
};

export default FicheLogement;
