import React from 'react';
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Carousel.css';

const CarouselComponent = ({ pictures, title }) => {
  const showArrows = pictures.length > 1;

  return (
    <div className="carousel-container">
      {showArrows && (
        <button className="carousel-chevron carousel-chevron-left">
          <FaChevronLeft />
        </button>
      )}
      <Carousel
        showArrows={false}
        showThumbs={false}
        showStatus={false}
        showIndicators={false}
        infiniteLoop={true}
        stopOnHover={false}
      >
        {pictures.map((picture, index) => (
          <div key={index}>
            <img src={picture} alt={`View of ${title} - ${index + 1}`} />
          </div>
        ))}
      </Carousel>
      {showArrows && (
        <button className="carousel-chevron carousel-chevron-right">
          <FaChevronRight />
        </button>
      )}
    </div>
  );
};

export default CarouselComponent;
