import React, { useEffect } from 'react';

const MyComponent = () => {
  useEffect(() => {
    const links = document.querySelectorAll(".home-nav-link");
  
    links.forEach(link => {
      link.addEventListener("click", function() {
        // Izbrishi podvlachuvanje od site linkovi
        links.forEach(l => {
          l.style.textDecoration = "none";
        });
        // Postavi podvlachuvanje na kliknatiot link
        this.style.textDecoration = "underline";
      });
    });

    // Treba da gi izbrisesh event listener-ite koga komponentata kje se unmount-ni
    return () => {
      links.forEach(link => {
        link.removeEventListener("click", () => {});
      });
    };
  }, []);

  return (
    <div>
      {/* Vashata struktura na komponentata tuka */}
    </div>
  );
};

export default MyComponent;
