import React from 'react';
import banner from '../assets/images/Banner1.png';
import Header from '../components/Header'; // Importez le composant Header
import Card from '../components/Card';
import '../styles/Home.css'; // Assurez-vous que le chemin est correct

const Home = () => {
  const cardData = [
    { title: 'Card 1', description: 'Description 1', imageUrl: 'https://via.placeholder.com/150' },
    { title: 'Card 2', description: 'Description 2', imageUrl: 'https://via.placeholder.com/150' },
    { title: 'Card 3', description: 'Description 3', imageUrl: 'https://via.placeholder.com/150' },
    { title: 'Card 4', description: 'Description 4', imageUrl: 'https://via.placeholder.com/150' },
    { title: 'Card 5', description: 'Description 5', imageUrl: 'https://via.placeholder.com/150' },
    { title: 'Card 6', description: 'Description 6', imageUrl: 'https://via.placeholder.com/150' },
  ];

  return (
    <div className="home">
      <Header /> {/* Utilisation du composant Header */}
      <div className="home-banner">
        <img src={banner} alt="Kasa banner" className="home-banner-img" />
      </div>
      <div className="home-content">
        <div className="cards-container">
          {cardData.map((card, index) => (
            <Card 
              key={index} 
              title={card.title} 
              description={card.description} 
              imageUrl={card.imageUrl} 
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
