import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Carousel.css';

const CarouselComponent = ({ pictures, title }) => {
  const showArrows = pictures.length > 1;

  // Custom render indicator function
  const renderIndicator = (onClickHandler, isSelected, index, label) => {
    if (pictures.length > 1) {
      return (
        <li
          className={`indicator ${isSelected ? "selected" : ""}`}
          onClick={onClickHandler}
          onKeyDown={onClickHandler}
          value={index}
          key={index}
          role="button"
          tabIndex={0}
          aria-label={`${label} ${index + 1}`}
        />
      );
    }
    return null; // Return null to hide indicators if there's only one image
  };

  return (
    <Carousel
      showArrows={showArrows}
      showThumbs={false}
      renderIndicator={renderIndicator}
      infiniteLoop={true}
      stopOnHover={false}
    >
      {pictures.map((picture, index) => (
        <div key={index}>
          <img src={picture} alt={`View of ${title} - ${index + 1}`} />
        </div>
      ))}
    </Carousel>
  );
};

export default CarouselComponent;
